import React, { useEffect, useState } from 'react';
import CreateUser from './CreateUser';
import EditUser from './EditUser';

const UserManagement = () => {
    const [showCreateUser, setCreateUser] = useState(false); // Estado para controlar si se muestra UserManagement
    const [showEditUser, setEditUser] = useState(false); 

    const handleCreateClick = () => {
        setCreateUser(!showCreateUser);
    }

    const handleEditClick = () => {
        setEditUser(!showEditUser);
    }
  // Lógica para cargar la lista de usuarios y manejar las acciones de creación, edición y eliminación de usuarios
  return (
    <div>
      <h2>Lista de Usuarios</h2>
      {/* Renderizar la lista de usuarios */}
      {/* Botones para Crear, Editar y Eliminar usuarios */}
      <button>Crear Usuario</button>
      <button>Editar Usuario</button>
      <button>Eliminar Usuario</button>
      {showCreateUser && <CreateUser onSave={handleCreateClick} onCancel={handleCreateClick}/>}
      {showEditUser && <EditUser/>}
    </div>
  );
};

export default UserManagement;
