import React, { useState } from 'react';
import './styles/App.css';
import NotesList from './components/NotesList';
import LogIn from './components/LogIn';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (userData) => {
    // Realizar acciones basadas en los datos del usuario
    console.log('Usuario inició sesión:', userData);
    // Actualizar el estado para indicar que el usuario ha iniciado sesión
    setIsLoggedIn(true);
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* Puedes agregar aquí cualquier contenido adicional del encabezado */}
      </header>

      {/* Mostrar el componente de inicio de sesión solo si el usuario no ha iniciado sesión */}
      {!isLoggedIn && <LogIn onLogin={handleLogin} />}

      {/* Mostrar el componente de lista de notas si el usuario ha iniciado sesión */}
      {isLoggedIn && <NotesList />}
    </div>
  );
}

export default App;
