import React, { useState } from 'react';
import './styles/App.css';
import NotesList from './components/NotesList';
import LogIn from './components/LogIn';
import UserManagement from './components/UserManagement';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showUserManagement, setShowUserManagement] = useState(false);
  const [showNotesList, setShowNotesList] = useState(true);

  const handleLogin = (userData) => {
    setIsLoggedIn(true);
    if (userData.username === 'admin') {
      setIsAdmin(true);
      setShowUserManagement(true);
    }
  };

  const handleUserManagement = () => {
    setShowUserManagement(true);
    setShowNotesList(false); // Ocultar NotesList
  };

  const handleExitUserManagement = () => {
    setShowUserManagement(false);
    setShowNotesList(true); // Mostrar NotesList nuevamente
  };

  return (
    <div className="App">
      <header className="App-header">
      </header>

      {!isLoggedIn && <LogIn onLogin={handleLogin} />}
      {isLoggedIn && showNotesList && <NotesList isAdmin={isAdmin} onUserManagement={handleUserManagement} />}
      {showUserManagement && <UserManagement onExit={handleExitUserManagement} />}
    </div>
  );
}

export default App;
