import React, { useState } from 'react';
import './styles/App.css';
import NotesList from './components/NotesList';
import LogIn from './components/LogIn';
import UserManagement from './components/UserManagement';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showUserManagement, setShowUserManagement] = useState(false);

  const handleLogin = (userData) => {
    setIsLoggedIn(true);
    if (userData.username === 'admin') {
      setIsAdmin(true);
      setShowUserManagement(true)
    }
  };

  const handleUserManagement = () => {
    setShowUserManagement(true);
  };

  const handleExitUserManagement = () => {
    setShowUserManagement(false);
  };

  return (
    <div className="App">
      <header className="App-header">

      </header>
      {!isLoggedIn && <Login onLogin={handleLogin} />}
      {isLoggedIn && !showUserManagement && <NotesList />}
      {showUserManagement && <UserManagement onClose={() => setShowUserManagement(false)} />}

      {/* Mostrar el botón de gestión de usuarios solo si el usuario está logueado y es administrador */}
      {isLoggedIn && isAdmin && !showUserManagement && <button onClick={handleUserManagement}>User Management</button>}
    </div>
  );
}

export default App;
