import React, { useState } from 'react';

const LogIn = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    fetch('http://localhost:3000/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Invalid username or password');
        }
        return response.json();
      })
      .then(data => {
        // Manejar la respuesta exitosa del backend, por ejemplo, almacenar el token de autenticación en el estado o en localStorage.
        console.log('Login successful:', data);
        onLogin(data); // Llamar a la función proporcionada por el padre para manejar el inicio de sesión
      })
      .catch(error => {
        // Manejar errores de red o errores específicos del backend
        console.error('Error during login:', error);
        setError(error.message);
      });
  };

  return (
    <div>
      <h2>Log In</h2>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
      {/* Mostrar mensaje de error si existe */}
      {error && <p>{error}</p>}
    </div>
  );
};

export default LogIn;
